int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);
	ll x;
    cin>>x;
    vector<ll> vec;
    for(ll f=2;f*f<=x;f++){
        ll tmp=1;
        while(x%f==0){
            tmp*=f;
            x/=f;
        }
        if(tmp!=1) vec.pb(tmp);
    }
    if(x>1)vec.pb(x);
    ll aa=INF,ab=INF;
    for(ll i=0;i<(1<<vec.size());i++){
        ll a=1,b=1;
        forn(j,vec.size()){
            if((i&(1<<j))>0) a*=vec[j];
            else b*=vec[j];
        }
        if(max(a,b)<max(aa,ab)){
            aa=a;
            ab=b;
        }
    }
    cout<<ab<<' '<<aa;
    return 0;
}Looping factors:#include <bits/stdc++.h>

