vector<vector<int>> G;
vector<int> c;
vector<int> dfs(int u){
    vector<int> order;
    for(auto it:G[u]){
        auto child_order=dfs(it);
        order.insert(order.end(),all(child_order));
    }
    if(size(order)<c[u]){
        cout<<"NO";
        exit(0);
    }
    order.insert(order.begin()+c[u],u);
    return order;
}
