    vector<int> f(sz(s));
    for(int i=1;i<sz(man)-1;i++){
        int l=(i-man[i]+1)/2,r=(i-1)/2;
        f[l]++;
        if(r<sz(s)-1) f[r+1]--;
    }
    partial_sum(all(f),f.begin());
    reverse(all(s));
    auto g=z_function(t+"#"+s);
    g.erase(g.begin(),g.begin()+sz(t)+1);
    reverse(all(g));
    g.erase(g.end()-1);
    g.insert(g.begin(),0);
    ll ans=0;
    forn(i,sz(s)) ans+=ll(f[i])*g[i];
    cout<<ans;
    return 0;
}

